import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.css'],
})
export class EventosComponent implements OnInit {
  /**
   * evento : any=  */
  public eventos: any =

  constructor() {}

  ngOnInit() { }

  /**
   * getEventos
   *
   * this.eventos =
   */
  public getEventos() {

  this.eventos = [
    {
      Tema: 'Ayrton Senna',
      Local: 'São Paulo',
    },
    {
      Tema: 'Nelson Piquet',
      Local: 'Rio de Janeiro',
    },
    {
      Tema: 'Roberto Moreno',
      Local: 'Brasilia',
    },
  ];

  }

}
<p>Eventos Component</p>

<div *ngFor="let item of eventos">
  <h1>
    <p>{{item.Tema}}</p>
  </h1>
  <h2>
    <p>{{item.Local}}</p>
  </h2>
  <hr/>
</div>
